mapper.select:
SELECT	CTN_DTLS
FROM 	HIST_CTN 
WHERE  	MENU_CD = #{MENU_CD}

mapper.insert:
		INSERT INTO HIST_CTN (
        	MENU_CD
        	,CTN_DTLS
			,RGST_EMP_NUM
			,RGST_DTIM
        )
        VALUES (
	        #{MENU_CD, jdbcType=VARCHAR}
			,#{CTN_DTLS, jdbcType=CLOB}
			,'admin'
			,sysdate
        )

mapper.update:
		UPDATE HIST_CTN
	        SET
			CTN_DTLS = #{CTN_DTLS, jdbcType=CLOB}
			,CORCT_EMP_NUM = #{RGST_EMP_NUM, jdbcType=VARCHAR}
			,CORCT_DTIM = sysdate
        WHERE MENU_CD = #{MENU_CD}
