mapper.select:
SELECT COUNT(*) AS CNT FROM HIST_MENU C
MENU_CATE = #{MENU_CATE}
CONNECT BY PRIOR C.MENU_CD = C.HI_MENU_CD AND LEVEL BETWEEN 0 AND 3 START WITH HI_MENU_CD IS NULL ORDER SIBLINGS BY C.MENU_SEQ ASC

mapper.delete:
DELETE FROM HIST_MENU
WHERE MENU_CD = #{MENU_CD}
AND MENU_CATE = #{MENU_CATE}
